mapper.select:
SELECT COUNT(1)
FROM t_payment
WHERE
  payment_no = #{paymentNo,jdbcType=VARCHAR}
  AND order_money = #{orderMoney,jdbcType=DECIMAL}

mapper.update:
UPDATE t_payment
SET
  payment_status = ${@cn.monstar.payment.domain.model.enums.PaymentStatusEnum@PAID.enumValue},
  out_trade_no = #{outTradeNo,jdbcType=VARCHAR}
WHERE payment_no = #{paymentNo,jdbcType=VARCHAR}
